import { CommonModule } from '@angular/common';
import { ChangeDetectorRef, Component, effect, inject } from '@angular/core';
import { FlightStepsComponent } from '../../../../../shared/flight-steps/flight-steps.component';
import { NzCardModule } from 'ng-zorro-antd/card';
import { NzIconModule } from 'ng-zorro-antd/icon';
import { NzTypographyModule } from 'ng-zorro-antd/typography';
import { NzAvatarModule } from 'ng-zorro-antd/avatar';
import { AuthService, FlightService } from '../../../../../services';
import {
  flightSignal,
  flightUserSignal,
} from '../../../../../core/data-values';
import { generateId } from '../../../../../helpers/generate-id';
import { generateDate } from '../../../../../helpers/generate-date';
import { generateLetter } from '../../../../../helpers/generate-letter';
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
import { TranslateModule } from '@ngx-translate/core';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-check-in-done',
  standalone: true,
  imports: [
    CommonModule,
    FlightStepsComponent,
    NzCardModule,
    NzAvatarModule,
    NzIconModule,
    NzTypographyModule,
    TranslateModule,
  ],
  templateUrl: './check-in-done.component.html',
  styleUrl: './check-in-done.component.less',
})
export class CheckInDoneComponent {
  flightService = inject(FlightService);
  authService = inject(AuthService);
  user: any = flightUserSignal();
  flight: any = flightSignal();
  ticketNo: any;
  flightHour: any;
  flightHourTime: any;
  formattedDate: any;
  formattedPhone: any;
  //flightFullTime:any;
  //flightFullTimeArrive:any;

  ngOnInit(): void {
    // console.log('flight signal:', this.flight);
    // console.log('user signal:', this.user);

    this.ticketNo = generateId.generateRandom13DigitNumber();
    this.flightHour = generateDate.generateDateToHour(
      flightSignal()?.dateFirst!
    );
    this.flightHourTime = generateDate.generateDateToHourFlight(
      flightSignal()?.dateFirst!
    );
    this.formattedDate = generateDate.generateFormattedDate(
      flightSignal()?.dateFirst!
    );
    this.formattedPhone = generateLetter.formatPhoneNumber(
      flightUserSignal()?.phone!
    );
    //this.flightFullTime = generateDate.generateFullDateTime(flightSignal()?.dateFirst!);
    //this.flightFullTimeArrive = generateDate.generateFullDateTimeArrive(flightSignal()?.dateFirst!);
  }

  downloadPDF() {
    const docDefinition: any = {
      content: [
        {
          columns: [
            {
              // Markanın adı
              text: 'AIR',
              style: 'header',
            },
            {
              // Logo (Base64 formatında)
              image:
                'data:image/png;base64,',
              width: 50,
              alignment: 'right',
            },
          ],
        },
        {
          text: 'Check-In başarılı. İyi seyahatler dileriz.',
          style: 'header',
          margin: [0, 0, 0, 20], // Üst, sağ, alt, sol boşluklar
        },
        {
          text: `Rezervasyon No (PNR): ${flightSignal()?.PNRNO}`,
          style: 'subheader',
          margin: [0, 0, 0, 10],
        },
        {
          text: 'Uçuş Bilgileri',
          style: 'subheader',
        },
        {
          text: 'Yolcu Bilgileri',
          style: 'subheader',
          margin: [0, 10, 0, 5],
        },
        {
          columns: [
            {
              text: `Ad: ${flightUserSignal()?.firstName?.toUpperCase()} ${flightUserSignal()?.lastName?.toUpperCase()}`,
              style: 'info',
            },
            { text: `Telefon: ${this.formattedPhone}`, style: 'info' },
            { text: `Email: ${flightUserSignal()?.email}`, style: 'info' },
          ],
        },
        {
          text: 'Seyahat Detayları',
          style: 'subheader',
          margin: [0, 10, 0, 5],
        },
        {
          table: {
            headerRows: 1,
            widths: ['*', '*', '*', '*'],
            body: [
              ['Kalkış Zamanı', 'Varış Zamanı', 'Uçuş No', 'Bilet No'],
              [
                `${this.flightHour} - ${this.formattedDate}`,
                `${this.flightHourTime} - ${this.formattedDate}`,
                'PC2024',
                `${this.ticketNo}`,
              ],
            ],
          },
          margin: [0, 10, 0, 10],
        },
        {
          text: `Paket Türü: ${
            flightSignal()?.packageFirst
          } model uçuş paketi seçilmiştir.`,
          style: 'info',
        },
        {
          text:
            flightSignal()?.packageFirst === 'Eco'
              ? 'Toplam Bagaj Hakkı: 15 Kg, Kabin Bagaj Hakkı: 55x40x20 cm'
              : flightSignal()?.packageFirst === 'Classic'
              ? 'Toplam Bagaj Hakkı: 20 Kg, Kabin Bagaj Hakkı: 55x40x20 cm'
              : 'Toplam Bagaj Hakkı: 25 Kg, Kabin Bagaj Hakkı: 55x40x20 cm',
          style: 'info',
          margin: [0, 5, 0, 5],
        },
        {
          // Alt açıklama yazıları
          text: 'İade veya değişiklik işlemleri için uçuşunuzdan 24 saat önce iletişime geçiniz.',
          style: 'footer',
          margin: [0, 20, 0, 0],
        },
        {
          text: 'Yolculuk sırasında lütfen biletinizi ve kimliğinizi yanınızda bulundurunuz.',
          style: 'footer',
          margin: [0, 5, 0, 0],
        },
      ],
      styles: {
        header: {
          fontSize: 18,
          bold: true,
          alignment: 'center',
        },
        subheader: {
          fontSize: 14,
          bold: true,
          margin: [0, 10, 0, 5],
        },
        info: {
          fontSize: 12,
          margin: [0, 5, 0, 5],
        },
        footer: {
          fontSize: 10,
          italics: true,
          alignment: 'center',
        },
      },
    };

    pdfMake.createPdf(docDefinition).download('check-in-bilgi.pdf');
  }
}
